    [Parameter]
        public string specializedId { get; set; }
        [Parameter]
        public string lecuId { get; set; }
        [Parameter]
        public string subjectId { get; set; }

        [Parameter]
        public string idclass { get; set; }

        [Parameter]
        public string idlesson { get; set; }

        private HomeWorkCreatVM sumit = new HomeWorkCreatVM();
        [Inject]
        public NavigationManager Navigation { get; set; }

        [Inject]
        public IHomeWorkClient _iHomeWorkClient { get; set; }
        
        [Inject] private IToastService _toastService { get; set; }
        public bool ShowAuthError { get; set; }
        [Parameter]
        public string Error { get; set; }


        private async Task Create() { 
            ShowAuthError = false;
            sumit.IdSubject = Guid.Parse(subjectId);
            sumit.IdClass = Guid.Parse(idclass);
            sumit.IdLesson = Guid.Parse(idlesson);
            sumit.IdUser = lecuId;
            var result = await _iHomeWorkClient.CreateCP(sumit);

            if (result != 1)
            {
                _toastService.ShowError("Mỗi 1 bài học chỉ được phép có 1 link giao bài hoặc Link bài tập đã tồn tại !");
                Navigation.NavigateTo($"/Homeworkcreate/{idclass}/{subjectId}/{idlesson}/{lecuId}/{specializedId}");
            }
            else
            {
                _toastService.ShowSuccess("Thêm thành công");
                Navigation.NavigateTo($"/HomeWorklist/{idclass}/{lecuId}/{subjectId}/{specializedId}/{idlesson}");
            }
        }
       
        private void ClearForm()
        {
            sumit.LinkHomeWork = "";
        }
        private void Quaylai()
        {
            Navigation.NavigateTo($"/HomeWorklist/{idclass}/{lecuId}/{subjectId}/{specializedId}/{idlesson}");
        }

