    void CheckManual(Guid st, bool check)
    {
        if (check == true)
        {
            foreach (var x in _lstSpecialized.Where(c => c.Id == st))
            {
                x.IsChecked = false;
            }
        }
        if (check == false)
        {
            foreach (var x in _lstSpecialized.Where(c => c.Id == st))
            {
                x.IsChecked = true;
            }
        }
    }
