    public async Task OnConfirmationCreate(bool deleteconfirmed)
    {
        if (deleteconfirmed)
        {
            await studentsApiClient.CreateStudentInClass(createStudentInClassDto);
            await Get();
            _toatser.ShowSuccess("Add Successfully To Class ");
            _navier.NavigateTo("/");
        }
    }

