    public bool checkName(string x)
    {
        foreach (char c in x)
        {
            if (!Char.IsLetter(c) && c.ToString() != " ")
                return false;
        }
        return true;
    }
    public bool checkCode(string x)
    {
        foreach (char c in x)
        {
            if (!Char.IsLetter(c) && !Char.IsDigit(c))
                return false;
        }
        return true;
    }
    public bool checkNumber(string x)
