            }
            else
            {
                var temp = lstSpecializedCP.FirstOrDefault(x => x.SpecializationName == request.SpecializationName && x.Level == 0);

                if (temp != null)
                {
                    ToastService.ShowError("Chuyên ngành đã tồn tại", "Error");
                    Navigation.NavigateTo("/createSpecializedUP");
                }
                else
                {
