    public async Task OnConfirmationDelete(bool deleteconfirmed)
    {
        if (deleteconfirmed && _lstIDRemoveRange.Count == 0)
        {
            NotifiToastService.ShowError("Bạn chưa chọn!");
            await GetExams();
            _navier.NavigateTo($"/ExamList");
